sys_variable_value.value:
(function execute(inputs, outputs) {
  var csv = !gs.nil(inputs.csv) ? inputs.csv.toString() : '';
  var strToAdd = inputs.strToAdd.toString();
  var delimiter = inputs.delimiter.toString();

  csv += csv == '' && !gs.nil(strToAdd) ? '' : delimiter;
  csv += strToAdd.toString();

  outputs.csv = csv;
})(inputs, outputs);

