sys_variable_value.value:
(function execute(inputs, outputs) {
    var taskId = inputs.taskid.toString();
    var notificationEvent = inputs.event.toString();

    gs.eventQueue('notification.event.queue', null, taskId, notificationEvent);
})(inputs, outputs);

