sys_variable_value.value:
(function execute(inputs, outputs) {
  var title = inputs.title;
  var body = inputs.body;
  var urls = '';
  var urlArray = inputs.urls;
  if(urlArray.length > 0){
    for(var i = 0; i < urlArray.length; i++){
      if(!gs.nil(urlArray[i].URL) && !gs.nil(urlArray[i].Label)){
        urls += '<a href="' + urlArray[i].URL + '"><button type="button" class="btn btn-primary">' + urlArray[i].Label + '</button></a>';
      }
    }
  }

  var html = '<div border="1px" style="max-width:500px;"><div style="background-color:#003087;color:white;"><b>' + title + '</b></div><div> ' + body + ' </div><div>' + urls + '</div></div>';

  outputs.html = html;
})(inputs, outputs);

