sys_variable_value.value:
(function execute(inputs, outputs) {
  try{
  var flowDataSysId = inputs.FlowDataSysId;
  var field = inputs.Field;
  var fieldValue = '';

//Get the variable value record
var flowDataGr = new GlideRecord('sys_flow_data');
flowDataGr.get(flowDataSysId);

var variableDefinition = flowDataGr.definition.toString();

//Get the variable name definition
var variableNameGr = new GlideRecord('sys_flow_data_var');
variableNameGr.addEncodedQuery('element=' + field + '^model_id=' + variableDefinition);
variableNameGr.query();

if (variableNameGr.next()) {
  var variableNameSysId = variableNameGr.getUniqueValue();
  //Get the variable Value Record
  var variableValueGr = new GlideRecord('sys_variable_value');
  variableValueGr.addEncodedQuery('document=sys_flow_data^variable=' + variableNameSysId + '^document_key=' + flowDataSysId);
  variableValueGr.query();
  //Set the variable value
  if (variableValueGr.next()) {
    fieldValue = variableValueGr.getValue('value');
    variableValueGr.update();
  }
}
  outputs.field_value = fieldValue.toString();
  }
  catch(e){
    outputs.field_value = e;
  }
})(inputs, outputs);

