{"html":{"scriptActive":true,"script":"	var notification = new ModularNotificationsBuilder();
		notification.hideUrls = fd_data.subflow_inputs.custom_template_values.hide_rRLs;
		notification.table = fd_data.subflow_inputs.related_record_table.toString();
		notification.documentID = fd_data.subflow_inputs.related_record_document_id.sys_id.toString();
		notification.setGlobalUrl();
		notification.name = fd_data.subflow_inputs.recipient_first_name.toString();
		notification.action = fd_data.subflow_inputs.template_action.toString();

 //custom information
 notification.customMoreInformation = fd_data.subflow_inputs.custom_template_values.more_information.toString() || '';
 notification.customInformation = fd_data.subflow_inputs.custom_template_values.information.toString() || '';
		notification.varList = fd_data.subflow_inputs.custom_template_values.Variables || [];
		notification.customSubject = fd_data.subflow_inputs.custom_template_values.subject_title || '';

		var html = notification.getHTML().toString();
		gs.info(html);
 return html;"}}

