sys_hub_input_scripts.script:
{"ah_body":{"scriptActive":true,"script":"	//These variables will control the different sections of the modular notifications
	//Subject will also control the email subject, don't include HTML there
	var notification = new ModularNotificationsBuilder();

	//URL that can be universally used for viewing a ticket on the portal
	notification.table = fd_data.subflow_inputs.table3;
	notification.documentID = fd_data.subflow_inputs.document_id;
	notification.setGlobalUrl();
	notification.action = 'custom';
	var link = notification.globalURL;

	//Email subject and subject section
	notification.subject = fd_data.subflow_inputs.subject1;

	//User's first name
	notification.name = fd_data.subflow_inputs.name;

	//Information section
	notification.information = fd_data.subflow_inputs.information;

	//variables section
	notification.varList = fd_data.subflow_inputs.varList;
	//More information section
	notification.moreInformation = fd_data.subflow_inputs.more_information;

	//Put it all together to get the full HTML template
	var html = notification.getHTML();

	//Print HTML to the body
	return html;"}}

