sys_hub_input_scripts.script:
{"number":{"scriptActive":true,"script":"var table = fd_data.subflow_inputs.table3.toString();
var docId = fd_data.subflow_inputs.document_id.toString();
var currentNumber = fd_data.flow_var.number.toString();

 //validate that the table exists
 var tableGr = new GlideRecord('sys_dictionary');
 tableGr.addQuery('internal_type=collection^name=' + table);
 tableGr.query();
 var tableExists = tableGr.next();
if(!tableExists){
 return currentNumber;
}
var taskGr = new GlideRecord(table);
if(taskGr.get(docId)){
 currentNumber = !gs.nil(taskGr.number) ? taskGr.number.toString() : currentNumber
}
return currentNumber;"}}

{"debug_info":{"scriptActive":true,"script":"try {
 var debugInfo = '';

 // Get table and tableId values from subflow inputs
 var table = fd_data.subflow_inputs.table3.toString();
 var tableId = fd_data.subflow_inputs.document_id.toString();

 // Initialize variables
 var varString = '';
 var subject = fd_data.subflow_inputs.subject1.toString();
 var recipientString = '';
 var recipientEmails = fd_data.subflow_inputs.recipient_csv_emails.toString();
 var recipientIds = fd_data.subflow_inputs.recipient_csv_sys_ids.toString();
 var varList = fd_data.subflow_inputs.varList;
 var recipientList = fd_data.subflow_inputs.recipients;
 // Build string for a list of variables
 for (var i = 0; i < varList.length; i++) {
 varString += JSON.stringify(varList[i]) + "\n";
 }

 // Build string for a list of recipients
 for (var j = 0; j < recipientList.length; j++) {
 recipientString += recipientList[j].name.toString() + "\n";
 } 

 // Build debugInfo string with variable names and values
 var debugInfo = 'varString: ' + varString + '\n' + 'subject: ' + subject + '\n';
 debugInfo += recipientString ? 'recipientString: ' + recipientString + '\n' : '';
 debugInfo += recipientEmails ? 'recipientEmails: ' + recipientEmails + '\n' : '';
 debugInfo += recipientIds ? 'recipientIds: ' + recipientIds + '\n' : '';
 var tableInfo = 'table: ' + table + '\n' + 'tableId: ' + tableId;

 //validate that the table exists
 var tableGr = new GlideRecord('sys_dictionary');
 tableGr.addQuery('internal_type=collection^name=' + table);
 tableGr.query();
 var tableExists = tableGr.next();

 // If table and tableId values are not empty, query the table for sys_id=tableId
 if (tableExists && !gs.nil(table) && !gs.nil(tableId)) {
 var gr = new GlideRecord(table);
 gr.get(tableId);
 gr.query();

 debugInfo += gr.next() && !gs.nil(gr.number) ? 'Task Number: ' + gr.number.toString() + '\n' : '';
 } 
 // If table or tableId value is empty, append their names and values to debugInfo
 else {
 debugInfo += tableInfo;
 }

 return debugInfo;
} 
// Catch any errors that occur and return a generic error message
catch (e) {
 return 'Failed to generate debug info: ' + e;
}"}}

