sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
    var incReq, incReqUpper, link, shortDesc;
    //Set incident/request upper and lower strings and the inc bool var

    if (current.trigger_table.toString().includes("incident")) {
		incReq = "incident";
        inc = true;
        shortDesc = '${task_id.short_description}';
    } else {
        incReq = "request";
        inc = false;
        //Get short description
        var gr = new GlideRecord("sc_req_item");
        gr.addQuery("request", current.task_id.sys_id);
        gr.query();
        var count = 0;
        gr.next();
        count++;
        shortDesc = gr.cat_item.name;
        //template.print (gr.short_description);
        if (count > 1) {
            shortDesc = count.toString() + ' requested items';
        }
    }
    incReqUpper = incReq;
    incReqUpper = incReqUpper.charAt(0).toUpperCase() + incReqUpper.slice(1);

    //Set links
    if (inc) {
        link = '/sp/?id=ticket&table=incident&sys_id=${task_id.sys_id}&sysparm_view=portal';
    } else {
        link = '/sp/?id=ticket&table=sc_request&view=sp&sys_id=${task_id.sys_id}';
    }

    template.print('<table width="100%"><tr><td width="20%" style="padding: 10px 10px 10px 10px;"><img src="/APiGroupLogo.png" width="100%" style="vertical-align: middle;"></td><td style="font-weight: 600; font-size: 20px;">How are we doing?<br>Feedback is a gift.</td><td width="20%"><img src="/SurveyTime.png" width="100%" style="vertical-align: middle;"></td></tr></table><table width="100%"><tr><td style="padding: 0px 25px 0px 25px;"><table style="padding: 10px 10px 10px 10px; border-top: medium solid #003087;" width="100%"><tr><td width="60%" style="border-right: medium solid #003087;"><div style="font-weight: 600; font-size: 20px;">${user.first_name} ${user.last_name},</div>Your ' + incReq + ' has been recently been resolved. Please take this short 60 second survey to share your experience.<br><br><div style="font-weight: 600;">' + incReqUpper + ': ' + shortDesc + '</div> <br><br><a href="/sp?id=take_survey&instance_id=${sys_id}"><img src="/TakeTheSurveyNow.png" width="200" style="vertical-align: middle;"></a><br><br><div style="font-weight: 600;"><br><br>Best Regards, <br>APi Field Support</div></td><td><div style="font-weight: 600; font-size: 20px;">' + incReqUpper + ' Information</div>${trigger_id}<br><br><div style="font-weight: 600; font-size: 20px;">' + incReqUpper + ' Status</div>${state}<br><br><div style="font-weight: 600; font-size: 20px;">Have any Questions?</div>We are here to help. <br><a href="' + link + '">Click Here</a> to add a comment to your ' + incReq + ' or give us a call at (651) 925-8511.<div align="right"></div></td></tr></table></td></tr></table>');
})(current, template, email, email_action, event);
